import roleApi from '@/api/Role.js';
import userApi from '@/api/User.js';
export default {
  name: 'UserRoleEdit',
  components: {},
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      userRole: this.initUserRole(),
      screenWidth: 0,
      width: this.initWidth(),
      userList: [],
      userIdList: [],
      disabled: false,
      rules: {},
      renderFunc: function renderFunc(h, option) {
        // return <span title='option.account - {option.name}'>{option.account} - {option.name}</span>
        return h("span", {
          "attrs": {
            "title": option.account + " - " + option.name
          }
        }, [option.account, " - ", option.name]);
      }
    };
  },
  computed: {
    isVisible: {
      get: function get() {
        return this.dialogVisible;
      },
      set: function set() {
        this.close();
        this.reset();
      }
    },
    title: function title() {
      return '分配角色成员';
    }
  },
  watch: {},
  mounted: function mounted() {
    var _this = this;
    this.initUserList();
    window.onresize = function () {
      return function () {
        _this.width = _this.initWidth();
      }();
    };
  },
  methods: {
    initUserRole: function initUserRole() {
      return {
        roleId: '',
        userIdList: []
      };
    },
    initWidth: function initWidth() {
      this.screenWidth = document.body.clientWidth;
      if (this.screenWidth < 991) {
        return '90%';
      } else if (this.screenWidth < 1400) {
        return '45%';
      } else {
        return '800px';
      }
    },
    initUserList: function initUserList() {
      var _this2 = this;
      userApi.page({
        current: 1,
        size: 100000,
        model: {
          state: true
        }
      }).then(function (response) {
        var res = response.data;
        _this2.userList = res.data.records;
      }).catch(function () {
        _this2.$message({
          message: _this2.$t('tips.getDataFail'),
          type: 'error'
        });
      });
    },
    setUserRole: function setUserRole(val) {
      var vm = this;
      vm.userRole.roleId = val.id;
      // vm.disabled = val.readonly
      roleApi.findUserIdByRoleId(val.id).then(function (response) {
        var res = response.data;
        vm.userRole.userIdList = res.data;
      });
    },
    close: function close() {
      this.$emit('close');
    },
    reset: function reset() {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate();
      this.$refs.form.resetFields();
      this.userRole = this.initUserRole();
      this.disabled = false;
    },
    submitForm: function submitForm() {
      var vm = this;
      this.$refs.form.validate(function (valid) {
        if (valid) {
          vm.editSubmit();
        } else {
          return false;
        }
      });
    },
    editSubmit: function editSubmit() {
      var vm = this;
      console.log(this.userRole);
      roleApi.saveUserRole(this.userRole).then(function (response) {
        var res = response.data;
        if (res.isSuccess) {
          vm.isVisible = false;
          vm.$message({
            message: vm.$t('tips.createSuccess'),
            type: 'success'
          });
          vm.$emit('success');
        }
      });
    },
    filterMethod: function filterMethod(query, item) {
      return item.name.indexOf(query) > -1 || item.account.indexOf(query) > -1;
    }
  }
};
