import userApi from "@/api/GlobalUser.js";
export default {
  name: "UserEdit",
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    },
    type: {
      type: String,
      default: "add"
    }
  },
  data: function data() {
    return {
      user: this.initUser(),
      screenWidth: 0,
      width: this.initWidth(),
      rules: {
        password: {
          required: true,
          message: this.$t("rules.require"),
          trigger: "blur"
        },
        confirmPassword: {
          required: true,
          message: this.$t("rules.require"),
          trigger: "blur"
        }
      }
    };
  },
  computed: {
    isVisible: {
      get: function get() {
        return this.dialogVisible;
      },
      set: function set() {
        this.close();
        this.reset();
      }
    },
    title: function title() {
      return "修改密码";
    }
  },
  mounted: function mounted() {
    var _this = this;
    window.onresize = function () {
      return function () {
        _this.width = _this.initWidth();
      }();
    };
  },
  methods: {
    initUser: function initUser() {
      return {
        id: "",
        confirmPassword: "",
        password: "",
        tenantCode: ""
      };
    },
    initWidth: function initWidth() {
      this.screenWidth = document.body.clientWidth;
      if (this.screenWidth < 991) {
        return "90%";
      } else if (this.screenWidth < 1400) {
        return "45%";
      } else {
        return "800px";
      }
    },
    setUser: function setUser(val) {
      var vm = this;
      vm.user = {
        id: val.id,
        tenantCode: val.tenantCode
      };
    },
    close: function close() {
      this.$emit("close");
    },
    reset: function reset() {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate();
      this.$refs.form.resetFields();
      this.user = this.initUser();
    },
    submitForm: function submitForm() {
      var vm = this;
      this.$refs.form.validate(function (valid) {
        if (valid) {
          vm.editSubmit();
        } else {
          return false;
        }
      });
    },
    editSubmit: function editSubmit() {
      var _this2 = this;
      userApi.reset(this.user).then(function (response) {
        var res = response.data;
        if (res.isSuccess) {
          _this2.isVisible = false;
          _this2.$message({
            message: _this2.$t("tips.updateSuccess"),
            type: "success"
          });
          _this2.$emit("success");
        }
      });
    }
  }
};
