import Pagination from "@/components/Pagination";
import ModelEdit from "./edit";
import activitiApi from "@/api/Activiti.js";
import {initQueryParams} from '@/utils/commons'

function subForm() {
  return {
    model: {
      isMine: true,
      isOver: null
    }
  }
}

export default {
  name: "InstantManage",
  components: {Pagination, ModelEdit},
  filters: {},
  data() {
    return {
      dialogVisible: false,
      dialogImageUrl: "",
      dialog: {
        isVisible: false,
        type: "add"
      },
      tableKey: 0,
      queryParams: initQueryParams(subForm()),
      suspendStateType: {
        start: '1',
        stop: '2'
      },
      isMine: [{
        value: true, label: '自己'
      }, {
        value: false, label: '所有人'
      }],
      isOver: [{
        value: null, label: '所有'
      }, {
        value: true, label: '已完成'
      }, {
        value: false, label: '未完成'
      }],
      selection: [],
      loading: false,
      tableData: {
        records: [],
        total: 0
      },
    };
  },
  computed: {},
  mounted() {
    this.fetch();
  },
  methods: {
    fetch(params = {}) {
      this.queryParams.current = params.current ? params.current : this.queryParams.current;
      this.queryParams.size = params.size ? params.size : this.queryParams.size;

      activitiApi.pageInstantReimbursement(this.queryParams).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.tableData.records = res.data.records;
          this.tableData.total = res.data.total;
        }
      }).finally(() => this.loading = false);
    },
    cellClick(row, column) {
      if (column['columnKey'] === "operation") {
        return;
      }
      let flag = false;
      this.selection.forEach((item) => {
        if (item.id === row.id) {
          flag = true;
          this.$refs.table.toggleRowSelection(row);
        }
      })

      if (!flag) {
        this.$refs.table.toggleRowSelection(row, true);
      }
    },
    editClose() {
      this.reset()
    },
    search() {
      this.fetch({
        ...this.queryParams
      });
    },
    reset() {
      this.$refs.table.clearSort();
      this.$refs.table.clearFilter();
      this.search();
    },
    singleUpdate(row) {
      const vm = this;
      var data = {
        instId: row.inst.data.id,
        suspendState: row.inst.data.suspendState
      }
      activitiApi.updateSuspendStateInst(data).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.$message({
            message: this.$t("tips.updateSuccess"),
            type: 'success'
          });
          this.search();
        } else {
          this.$message({
            message: response.msg,
            type: 'error'
          });
        }
      }).finally(() => this.loading = false);
    },
    singleFormView(row) {
      this.$refs.modelEdit.open(row)
    },
    singleDelete(row) {
      this.$confirm(this.$t("tips.confirmDelete"), this.$t("common.tips"), {
        confirmButtonText: this.$t("common.confirm"),
        cancelButtonText: this.$t("common.cancel"),
        type: "warning"
      })
        .then(() => {
          this.delete([row.id]);
        })
        .catch(() => {
          this.reset();
        });
    },
    delete(data) {
      activitiApi.deleteInstReimbursement(data).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.$message({
            message: this.$t("tips.deleteSuccess"),
            type: 'success'
          });
          this.search();
        } else {
          this.$message({
            message: response.msg,
            type: 'error'
          });
        }
      }).finally(() => this.loading = false);
    },
    singleDetail(row) {
      this.singleFormView(row)
    },
    getFormQuery() {
      activitiApi.formQuery({}).then(response => {
        const res = response.data;
      }).finally(() => this.loading = false);
    }
  }
};
