import Pagination from "@/components/Pagination"
import TenantEdit from "./edit"
import TenantView from "./view"
import TenantConnection from "./connection"
import tenantApi from "@/api/Tenant.js"
import elDragDialog from '@/directive/el-drag-dialog'
import { downloadFile, initEnums, initQueryParams } from '@/utils/commons'
export default {
  name: "TenantManage",
  directives: { elDragDialog },
  components: { TenantConnection, Pagination, TenantEdit, TenantView },
  filters: {
    typeFilter (status) {
      const map = {
        CREATE: "success",
        REGISTER: "danger"
      }
      return map[status] || "info"
    },
    statusFilter (status) {
      const map = {
        NORMAL: "success",
        FORBIDDEN: "warning",
        WAITING: "info",
        REFUSE: "danger"
      }
      return map[status] || ""
    }
  },
  data () {
    return {
      dialog: {
        isVisible: false,
        title: ""
      },
      preview: {
        isVisible: false,
        context: ''
      },
      tenantViewVisible: false,
      tenantConnectionVisible: false,
      tableKey: 0,
      queryParams: initQueryParams({}),
      selection: [],
      loading: false,
      tableData: {
        total: 0
      },
      enums: {
        TenantTypeEnum: {},
        TenantStatusEnum: {}
      }
    }
  },
  computed: {
    currentUser () {
      return this.$store.state.account.user
    },
    typeFilterList () {
      const list = []
      for (const key in this.enums.TenantTypeEnum) {
        list.push({
          value: key,
          text: this.enums.TenantTypeEnum[key]
        })
      }
      return list
    },
    statusFilterList () {
      const list = []
      for (const key in this.enums.TenantStatusEnum) {
        list.push({
          value: key,
          text: this.enums.TenantStatusEnum[key]
        })
      }
      return list
    }
  },
  mounted () {
    initEnums(['TenantStatusEnum', 'TenantTypeEnum'], this.enums)
    this.fetch()
  },
  methods: {
    tenantConnectionClose () {
      this.tenantConnectionVisible = false
    },
    viewClose () {
      this.tenantViewVisible = false
    },
    editClose () {
      this.dialog.isVisible = false
    },
    editSuccess () {
      this.search()
    },
    onSelectChange (selection) {
      this.selection = selection
    },
    search () {
      this.fetch({
        ...this.queryParams
      })
    },
    reset () {
      this.queryParams = initQueryParams({})
      this.$refs.table.clearSort()
      this.$refs.table.clearFilter()
      this.search()
    },
    exportExcelPreview () {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }
      this.queryParams.extra.fileName = '导出用户数据'
      tenantApi.preview(this.queryParams).then(response => {
        const res = response.data
        this.preview.isVisible = true
        this.preview.context = res.data
      })
    },
    exportExcel () {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }
      this.queryParams.extra.fileName = '导出用户数据'
      tenantApi.export(this.queryParams).then(response => {
        downloadFile(response)
      })
    },
    add () {
      if (this.tableData.total > 5) {
        this.$message({
          message: '演示环境有限，最多支持创建5个租户，请在现有租户下创建用户进行测试',
          type: "warning",
          duration: 5000
        })
        return
      }
      this.$refs.edit.type = "add"
      this.dialog.title = this.$t("common.add")
      this.dialog.isVisible = true
    },
    singleDelete (row) {
      this.$refs.table.clearSelection()
      this.$refs.table.toggleRowSelection(row, true)
      this.batchDelete()
    },
    batchDelete () {
      if (!this.selection.length) {
        this.$message({
          message: this.$t("tips.noDataSelected"),
          type: "warning"
        })
        return
      }

      const readonlyIndex = this.selection.findIndex(item => item.readonly)

      if (readonlyIndex > -1) {
        this.$message({
          message: this.$t("tips.readonly"),
          type: "warning"
        })
        return
      }

      this.$confirm("删除租户数据后，会将租户库及其所有数据全部删除，建议调用禁用接口。", this.$t("common.tips"), {
        confirmButtonText: "禁用",
        cancelButtonText: "删除",
        type: "warning",
        closeOnClickModal: false,
        closeOnPressEscape: false,
        distinguishCancelAndClose: true
      })
        .then(() => {
          const ids = []
          let contain = false
          this.selection.forEach(item => {
            if (item.readonly) {
              contain = true
              return
            }
            ids.push(item.id)
          })
          if (contain) {
            this.$message({
              message: this.$t("tips.readonly"),
              type: "warning"
            })
          } else {
            this.updateStatus(ids)
          }
        })
        .catch((action) => {
          if (action === 'cancel') {
            const ids = []
            let contain = false
            this.selection.forEach(item => {
              if (item.readonly) {
                contain = true
                return
              }
              ids.push(item.id)
            })
            if (contain) {
              this.$message({
                message: this.$t("tips.readonly"),
                type: "warning"
              })
            } else {
              this.delete(ids)
            }
          }
        })
    },
    clearSelections () {
      this.$refs.table.clearSelection()
    },
    delete (ids) {
      tenantApi.remove(ids).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: this.$t("tips.deleteSuccess"),
            type: "success"
          })
          this.search()
        }
      })
    },
    updateStatus (ids) {
      tenantApi.updateStatus({ 'ids[]': ids }).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: '禁用成功',
            type: "success"
          })
          this.search()
        }
      })
    },
    changeStatus (row) {
      let status = 'NORMAL'
      if (row.status['code'] === 'NORMAL') {
        status = 'FORBIDDEN'
      } else {
        status = 'NORMAL'
      }
      tenantApi.updateStatus({ 'ids[]': row.id, status: status }).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: row.status['code'] === 'NORMAL' ? '禁用成功' : '启用成功',
            type: row.status['code'] === 'NORMAL' ? 'warning' : 'success'
          })
          this.search()
        }
      })
    },
    view (row) {
      this.$refs.view.setTenant(row)
      this.tenantViewVisible = true
    },
    edit (row) {
      if (row.readonly) {
        this.$message({
          message: this.$t("tips.readonly"),
          type: "warning"
        })
        return
      }
      this.$refs.edit.setTenant(row)
      this.$refs.edit.type = "edit"
      this.dialog.title = this.$t("common.edit")
      this.dialog.isVisible = true
    },
    fetch (params = {}) {
      this.loading = true
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }

      this.queryParams.current = params.current ? params.current : this.queryParams.current
      this.queryParams.size = params.size ? params.size : this.queryParams.size

      tenantApi.page(this.queryParams).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.tableData = res.data
        }
        // eslint-disable-next-line no-return-assign
      }).finally(() => this.loading = false)
    },
    sortChange (val) {
      this.queryParams.sort = val.prop
      this.queryParams.order = val.order
      if (this.queryParams.sort) {
        this.search()
      }
    },
    filterChange (filters) {
      for (const key in filters) {
        if (key.includes('.')) {
          const val = {}
          val[key.split('.')[1]] = filters[key][0]
          this.queryParams.model[key.split('.')[0]] = val
        } else {
          this.queryParams.model[key] = filters[key][0]
        }
      }
      this.search()
    },
    cellClick (row, column) {
      if (column['columnKey'] !== "selectionId") {
        return
      }
      let flag = false
      this.selection.forEach((item) => {
        if (item.id === row.id) {
          flag = true
          this.$refs.table.toggleRowSelection(row)
        }
      })

      if (!flag) {
        this.$refs.table.toggleRowSelection(row, true)
      }
    },
    initConnection (row) {
      this.$refs.connection.setTenant(row)
      this.tenantConnectionVisible = true
    }
  }
}
