import globalUserApi from '@/api/GlobalUser.js'
import tenantApi from '@/api/Tenant.js'

export default {
  name: 'GlobalUserEdit',
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: ''
    }
  },
  data () {
    return {
      tenantList: [],
      type: 'add',
      user: this.initUser(),
      screenWidth: 0,
      width: this.initWidth(),
      rules: {
        account: [
          { required: true, message: this.$t('rules.require'), trigger: 'blur' },
          { min: 4, max: 30, message: this.$t('rules.range4to10'), trigger: 'blur' },
          {
            validator: (rule, value, callback) => {
              if (!this.user.id && this.user.tenantCode) {
                const checkData = {
                  tenantCode: this.user.tenantCode,
                  account: value
                }
                globalUserApi.check(checkData)
                  .then((response) => {
                    const res = response.data
                    if (res.data) {
                      callback(this.$t('rules.usernameExist'))
                    } else {
                      callback()
                    }
                  })
              } else {
                callback()
              }
            }, trigger: 'blur'
          }
        ],
        tenantCode: [{ required: true, message: this.$t('rules.require'), trigger: 'blur' }]
      }
    }
  },
  computed: {
    isVisible: {
      get () {
        return this.dialogVisible
      },
      set () {
        this.close()
        this.reset()
      }
    }
  },
  mounted () {
    this.initTenantList()
    window.onresize = () => {
      return (() => {
        this.width = this.initWidth()
      })()
    }
  },
  methods: {
    initTenantList () {
      tenantApi.list({status: {code: 'NORMAL'}})
        .then((response) => {
          const res = response.data
          this.tenantList = res.data
        })
    },
    initUser () {
      return {
        id: '',
        tenantCode: '0000',
        name: '',
        account: '',
        mobile: '',
        email: '',
        password: '',
        confirmPassword: ''
      }
    },
    initWidth () {
      this.screenWidth = document.body.clientWidth
      if (this.screenWidth < 991) {
        return '90%'
      } else if (this.screenWidth < 1400) {
        return '45%'
      } else {
        return '800px'
      }
    },
    setUser (val) {
      this.user = { ...val }
    },
    close () {
      this.$emit('close')
    },
    submitForm () {
      this.$refs.form.validate((valid) => {
        if (valid) {
          if (this.type === 'add') {
            this.save()
          } else {
            this.update()
          }
        } else {
          return false
        }
      })
    },
    save () {
      globalUserApi.save(this.user)
        .then((response) => {
          const res = response.data
          if (res.isSuccess) {
            this.isVisible = false
            this.$message({
              message: this.$t('tips.createSuccess'),
              type: 'success'
            })
            this.$emit('success')
          }
        })
    },
    update () {
      globalUserApi.update(this.user)
        .then((response) => {
          const res = response.data
          if (res.isSuccess) {
            this.isVisible = false
            this.$message({
              message: this.$t('tips.updateSuccess'),
              type: 'success'
            })
            this.$emit('success')
          }
        })
    },
    reset () {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate()
      this.$refs.form.resetFields()
      this.user = this.initUser()
    }
  }
}
