import Pagination from "@/components/Pagination";
import msgApi from "@/api/Msg.js";
import {convertEnum} from "@/utils/utils";
import elDragDialog from '@/directive/el-drag-dialog'
import FileImport from "@/components/lamp/Import"
import {downloadFile, initMsgEnums, initQueryParams} from '@/utils/commons'


export default {
  name: "MsgList",
  directives: {elDragDialog},
  components: {Pagination, FileImport},
  filters: {
    statusFilter(status) {
      const map = {
        WAITING: "danger",
        SUCCESS: "success",
        FAIL: "error"
      };
      return map[status] || "success";
    }
  },
  data() {
    return {
      dialog: {
        isVisible: false,
        type: "add"
      },
      preview: {
        isVisible: false,
        context: ''
      },
      fileImport: {
        isVisible: false,
        type: "import",
        action: `${process.env.VUE_APP_BASE_API}/msg/msg/import`
      },
      tableKey: 0,
      queryParams: initQueryParams({
        model: {
          msgType: {code: null},
          bizType: {code: null},
        }
      }),
      selection: [],
      loading: false,
      tableData: {
        total: 0
      },
      enums: {
        MsgType: {},
        MsgBizType: {},
      },
    };
  },
  computed: {
    msgTypeFilters() {
      return convertEnum(this.enums.MsgType);
    },
    bizTypeFilters() {
      return convertEnum(this.enums.MsgBizType);
    }
  },
  watch: {
    '$route'(to) {
      if (to.path === '/resources/msg') {
        this.fetch();
      }
    }
  },
  mounted() {
    initMsgEnums(['MsgType', 'MsgBizType'], this.enums)
    this.fetch();
  },
  methods: {
    filterStatus(value, row) {
      return row.status === value;
    },
    onSelectChange(selection) {
      this.selection = selection;
    },
    search() {
      this.fetch({
        ...this.queryParams
      });
    },
    reset() {
      this.queryParams = initQueryParams({
        model:{
          msgType: {code: null},
          bizType: {code: null},
        }
      });
      this.$refs.table.clearSort();
      this.$refs.table.clearFilter();
      this.search();
    },
    exportExcelPreview() {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0];
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1];
      }
      this.queryParams.extra.fileName = '导出消息数据';
      msgApi.preview(this.queryParams).then(response => {
        const res = response.data;
        this.preview.isVisible = true;
        this.preview.context = res.data;
      });
    },
    exportExcel() {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0];
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1];
      }
      this.queryParams.extra.fileName = '导出消息数据';
      msgApi.export(this.queryParams).then(response => {
        downloadFile(response);
      });
    },
    importExcel() {
      this.fileImport.type = "upload";
      this.fileImport.isVisible = true;
      this.$refs.import.setModel(false);
    },
    importSuccess() {
      this.search();
    },
    importClose() {
      this.fileImport.isVisible = false;
    },
    singleDelete(row) {
      this.$refs.table.clearSelection()
      this.$refs.table.toggleRowSelection(row, true);
      this.batchDelete();
    },
    batchDelete() {
      if (!this.selection.length) {
        this.$message({
          message: this.$t("tips.noDataSelected"),
          type: "warning"
        });
        return;
      }
      this.$confirm(this.$t("tips.confirmDelete"), this.$t("common.tips"), {
        confirmButtonText: this.$t("common.confirm"),
        cancelButtonText: this.$t("common.cancel"),
        type: "warning"
      })
        .then(() => {
          const ids = [];
          this.selection.forEach(u => {
            ids.push(u.id);
          });
          this.delete(ids);
        })
        .catch(() => {
          this.clearSelections();
        });
    },
    clearSelections() {
      this.$refs.table.clearSelection();
    },

    delete(ids) {
      msgApi.delete(ids).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.$message({
            message: this.$t("tips.deleteSuccess"),
            type: "success"
          });
        }
        this.search();
      });
    },
    batchMark() {
      if (!this.selection.length) {
        this.$message({
          message: this.$t("tips.noDataSelected"),
          type: "warning"
        });
        return;
      }
      this.$confirm("确认全部标记为已读吗？", this.$t("common.tips"), {
        confirmButtonText: this.$t("common.confirm"),
        cancelButtonText: this.$t("common.cancel"),
        type: "warning"
      })
        .then(() => {
          const ids = [];
          this.selection.forEach(u => {
            ids.push(u.id);
          });
          this.mark(ids, ()=>{
            this.search();
          });
        })
        .catch(() => {
          this.clearSelections();
        });
    },
    mark(ids, callback) {
      msgApi.mark(ids).then(response => {
        const res = response.data;
        if (typeof callback === "function") {
          callback(ids);
        }
      });
    },
    view(row) {
      if (row.isRead) {
        this.$router.push({
          path: "/resources/msg/edit",
          query: {
            id: row.id,
            type: "view"
          }
        });
      } else {
        this.mark([row.id], ids => {
          this.$router.push({
            path: "/resources/msg/edit",
            query: {
              id: row.id,
              type: "view"
            }
          });
        });
      }
    },
    edit(row) {
      this.$router.push({
        path: "/resources/msg/edit",
        query: {
          id: row.id,
          type: "edit"
        }
      });
    },
    fetch(params = {}) {
      this.loading = true;
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0];
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1];
      }

      this.queryParams.current = params.current ? params.current : this.queryParams.current;
      this.queryParams.size = params.size ? params.size : this.queryParams.size;

      msgApi.page(this.queryParams).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.tableData = res.data;
        }
      }).finally(() => this.loading = false);
    },
    sortChange(val) {
      this.queryParams.sort = val.prop;
      this.queryParams.order = val.order;
      if (this.queryParams.sort) {
        this.search();
      }
    },
    filterChange(filters) {
      for (const key in filters) {
        if (key.includes('.')) {
          const val = {};
          val[key.split('.')[1]] = filters[key][0];
          this.queryParams.model[key.split('.')[0]] = val;
        } else {
          this.queryParams.model[key] = filters[key][0]
        }
      }
      this.search()
    },
    cellClick(row, column) {
      if (column['columnKey'] === "operation") {
        return;
      }
      let flag = false;
      this.selection.forEach((item) => {
        if (item.id === row.id) {
          flag = true;
          this.$refs.table.toggleRowSelection(row);
        }
      })

      if (!flag) {
        this.$refs.table.toggleRowSelection(row, true);
      }
    }
  }
};
