import tenantApi from '@/api/Tenant.js'
import datasourceConfigApi from '@/api/DatasourceConfig.js'

export default {
  name: 'TenantConnection',
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    },
    title: {
      type: String,
      default: ''
    }
  },
  data() {
    return {
      type: 'add',
      tenant: this.initTenant(),
      screenWidth: 0,
      datasourceConfigList: [],
      width: this.initWidth(),
      confirmDisabled: false,
      connectTypeDisabled: process.env.VUE_APP_IS_MULTI_TENANT_TYPE !== 'DATASOURCE',
      rules: {
        tenant: [
          {required: true, message: this.$t('rules.require'), trigger: 'blur'}
        ],
        connectType: [
          {required: true, message: this.$t('rules.require'), trigger: 'blur'}
        ],
        baseDatasourceId: {
          validator: (rule, value, callback) => {
            if (this.tenant.connectType === 'CUSTOM' && !value) {
              callback('请选择基础库数据源')
            } else {
              callback()
            }
          }, trigger: 'blur'
        },
        extendDatasourceId: {
          validator: (rule, value, callback) => {
            if (this.tenant.connectType === 'CUSTOM' && !value) {
              callback('请选择扩展库数据源')
            } else {
              callback()
            }
          }, trigger: 'blur'
        },
      }
    }
  },
  computed: {
    isVisible: {
      get() {
        return this.dialogVisible
      },
      set() {
        this.close()
        this.reset()
      }
    }
  },
  mounted() {
    window.onresize = () => {
      return (() => {
        this.width = this.initWidth()
      })()
    }
    this.loadDatasourceConfigList()
  },
  methods: {
    loadDatasourceConfigList() {
      if(process.env.VUE_APP_IS_MULTI_TENANT_TYPE === "DATASOURCE"){
        datasourceConfigApi.query()
          .then((response) => {
            if (response.data.isSuccess) {
              this.datasourceConfigList = response.data.data
            }
          })
      }
    },
    initTenant() {
      return {
        id: '',
        connectType: 'SYSTEM',
        tenant: '',
        baseDatasourceId: null,
        extendDatasourceId: null,
      }
    },
    initWidth() {
      this.screenWidth = document.body.clientWidth
      if (this.screenWidth < 991) {
        return '90%'
      } else if (this.screenWidth < 1400) {
        return '45%'
      } else {
        return '800px'
      }
    },
    setTenant(val) {
      this.tenant.id = val['id']
      this.tenant.tenant = val['code']
    },
    close() {
      this.$emit('close')
    },
    submitForm() {
      this.$refs.form.validate((valid) => {
        if (valid) {
          this.confirmDisabled = true
          this.initConnect()
        } else {
          return false
        }
      })
    },
    initConnect() {
      const loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      tenantApi.initConnect(this.tenant)
        .then((response) => {
          const res = response.data
          if (res.isSuccess && res.data) {
            this.isVisible = false
            this.$message({
              message: this.$t('tips.createSuccess'),
              type: 'success'
            })
            this.$emit('success')
          }
        }).finally(() => {
        this.confirmDisabled = false
        loading.close();
        return true
      })
    },
    reset() {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate()
      this.$refs.form.resetFields()
      this.tenant = this.initTenant()
    },
    connectTypeChange(val) {
      if (val === 'SYSTEM') {
        this.tenant.baseDatasourceId = null
        this.tenant.extendDatasourceId = null
      }
    }
  }
}
