.tags-view-container {
  height: 35px;
  width: 100%;
  background: #fff;
  border-bottom: 1px solid #f1f1f1;
  .tags-view-wrapper {
    .tags-view-item {
      display: inline-block;
      position: relative;
      cursor: pointer;
      height: 34px;
      line-height: 34px;
      border-left: 1px solid #f1f1f1;
      color: #999;
      background: #fff;
      padding: 0 20px;
      font-size: 13px;
      transition: all 0.3s;
      &:hover {
        color: #495060;
        & .el-icon-close {
          color: red;
        }
      }
      &:first-of-type {
      }
      &:last-of-type {
        border-right: 1px solid #f1f1f1;
      }
      &.active {
        /*background-color: #42b983;*/
        /*color: #fff;*/
        /*border-color: #42b983;*/
        &::before {
          content: '';
          background: #85ef47 !important;
          box-shadow: 0 0 7px #b7eb8f;
          display: inline-block;
          bottom: 3px;
          width: 5px;
          height: 5px;
          border-radius: 50%;
          position: relative;
          margin-right: 4px;
        }
      }
    }
  }
  .contextmenu {
    margin: 0;
    background: #fff;
    z-index: 3000;
    position: absolute;
    list-style-type: none;
    padding: 5px 0;
    border-radius: 4px;
    font-size: 13px;
    font-weight: 400;
    color: #333;
    box-shadow: 0 2px 12px 0 rgba(0, 0, 0, 0.1);
    li {
      margin: 0;
      padding: 7px 20px;
      cursor: pointer;
      &:hover {
        background-color: #e8f4ff;
        color: #46a6ff;
      }
    }
  }
}
