import activitiApi from "@/api/Activiti.js";
function subForm(){
  return {
    startTime: '',
    endTime: '',
    whenLong: '',
    type: '',
    reason: ''
  }
}
export default {
  name: 'ModelEdit',
  props: {
    type: {
      type: String,
      default: 'add'
    }
  },
  data () {
    return {
      editVisible: false,
      typs: ["市内交通费","长途出差费"],
      subForm: subForm(),
      row: {},
      tableData: [],
      tableKey: 'id',
      loading: false
    }
  },
  computed: {

  },
  watch: {

  },
  mounted () {
  },
  methods: {
    open(row) {
      this.editVisible = true
      this.row = row
      if (row) {
        this.getDetail()
        this.getList()
      }
    },
    editClose() {
      this.editVisible = false
      this.$parent.editClose()
      this.row = {}
      this.subForm = subForm()
      this.formItemList = []
      this.formList = []
      this.tableData = []
      this.formKey = null
    },
    getList() {
      const vm = this
      if (!vm.row.id) {
        return
      }
      vm.loading = true
      activitiApi.getItemList({ instId: vm.row.inst.key}).then(response => {
        const res = response.data;
        if (res.code === 0 && res.data) {
          vm.tableData = res.data
          vm.getReadyTaskByInst(vm.row.inst.key)
        }
      }).finally();
    },
    getDetail() {
      const vm = this
      if (!vm.row.id) {
        return
      }
      activitiApi.getDetailInstReimbursement({ id: vm.row.id}).then(response => {
        const res = response.data;
        if (res.code === 0) {
          vm.subForm = res.data
        }
      }).finally();
    },
    getReadyTaskByInst(instId) {
      const vm = this
      activitiApi.getReadyTaskByInst({ instId: instId}).then(response => {
        const res = response.data;
        if (res.code === 0 && res.data) {
          if (res.data) {
            let list = res.data
            list.forEach(data => {
              data.isReady = true
              data.itemName = data.name
              vm.tableData.unshift(data)
            })
          }
        }
      }).finally(() => this.loading = false);
    },
    submitForm() {
      const vm = this
      activitiApi.saveInstantReimbursement( vm.subForm ).then(response => {
        const res = response.data;
          if (res.isSuccess) {
            vm.$message({
              message: vm.$t("tips.createSuccess"),
              type: 'success'
            });
            vm.editClose()
          } else {
            vm.$message({
              message: response.msg,
              type: 'error'
            });
          }
      }).finally(() => vm.loading = false);
    },
    tableRowClassName({row, rowIndex}) {
      if (rowIndex === 0) {
        if (row.isReady) {
          return 'warning-row';
        } else {
          return 'success-row';
        }
      }

      return '';
    }
  }
}
