import parameterApi from "@/api/Parameter.js";

export default {
  name: "ParameterEdit",
  components: {  },
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    },
    type: {
      type: String,
      default: "add"
    }
  },
  data() {
    return {
      parameter: this.initParameter(),
      screenWidth: 0,
      width: this.initWidth(),
      rules: {
        key: [
          {required: true, message: this.$t("rules.require"), trigger: "blur"},
          { min: 1, max: 255, message: '最多不能超过255字符', trigger: "blur"}
        ],
        name: [
          {required: true, message: this.$t("rules.require"), trigger: "blur"},
          { min: 1, max: 255, message: '最多不能超过255字符', trigger: "blur"}
        ],
        value: [
          { max: 255, message: '最多不能超过255字符', trigger: "blur"}
        ],
      }
    };
  },
  computed: {
    isVisible: {
      get() {
        return this.dialogVisible;
      },
      set() {
        this.close();
        this.reset();
      }
    },
    title() {
      return this.$t("common." + this.type);
    }
  },
  watch: {},
  mounted() {
    window.onresize = () => {
      return (() => {
        this.width = this.initWidth();
      })();
    };
  },
  methods: {
    initParameter() {
      return {
        id: "",
        key: '',
        name: '',
        value: '',
        describe: '',
        readonly: false,
      };
    },
    initWidth() {
      this.screenWidth = document.body.clientWidth;
      if (this.screenWidth < 991) {
        return "90%";
      } else if (this.screenWidth < 1400) {
        return "45%";
      } else {
        return "800px";
      }
    },
    setParameter(val) {
      const vm = this;
      if (val) {
        vm.parameter = { ...vm.parameter, ...val };
      }
    },
    close() {
      this.$emit("close");
    },
    reset() {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate();
      this.$refs.form.resetFields();
      this.parameter = this.initParameter();
    },
    submitForm() {
      const vm = this;
      this.$refs.form.validate(valid => {
        if (valid) {
          vm.editSubmit();
        } else {
          return false;
        }
      });
    },
    editSubmit() {
      const vm = this;
      if (vm.type === "edit") {
          vm.update();

      } else {
          vm.save();
      }
    },
    save() {
      const vm = this;
      parameterApi.save(this.parameter).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          vm.isVisible = false;
          vm.$message({
            message: vm.$t("tips.createSuccess"),
            type: "success"
          });
          vm.$emit("success");
        }
      });
    },
    update() {
      parameterApi.update(this.parameter).then(response => {
        const res = response.data;
        if (res.isSuccess) {
          this.isVisible = false;
          this.$message({
            message: this.$t("tips.updateSuccess"),
            type: "success"
          });
          this.$emit("success");
        }
      });
    }
  }
};
