import '@riophae/vue-treeselect/dist/vue-treeselect.css'
import applicationApi from '@/api/Application.js'
import {initEnums} from '@/utils/commons.js'

export default {
  name: 'ApplicationEdit',
  components: {},
  props: {
    dialogVisible: {
      type: Boolean,
      default: false
    },
    type: {
      type: String,
      default: 'add'
    }
  },
  data() {
    return {
      remoteApplicationLoading: false,
      application: this.initApplication(),
      screenWidth: 0,
      width: this.initWidth(),
      orgList: [],
      enums: {ApplicationAppTypeEnum: {}},
      applicationList: [],
      rules: {
        name: [
          {required: true, message: this.$t('rules.require'), trigger: 'blur'},
          {min: 1, max: 255, message: this.$t('rules.range4to10'), trigger: 'blur'}
        ],
        state: {required: true, message: this.$t('rules.require'), trigger: 'blur'}

      },
      serviceList: [
        {id: "lamp-authority-server", name: "权限服务"},
        {id: "lamp-file-server", name: "文件服务"},
        {id: "lamp-msg-server", name: "消息服务"},
        {id: "lamp-demo-server", name: "演示服务"},
        {id: "lamp-order-server", name: "订单服务"},
      ]
    }
  },
  computed: {
    isVisible: {
      get() {
        return this.dialogVisible
      },
      set() {
        this.close()
        this.reset()
      }
    },
    title() {
      return this.type === 'add' ? this.$t('common.add') : this.$t('common.edit')
    }
  },
  watch: {},
  mounted() {
    initEnums(['ApplicationAppTypeEnum'], this.enums)
    window.onresize = () => {
      return (() => {
        this.width = this.initWidth()
      })()
    }
  },
  methods: {
    initApplication() {
      return {
        id: '',
        clientId: '',
        clientSecret: '',
        website: '',
        name: '',
        icon: '',
        describe: '',
        state: true,
      }
    },
    initWidth() {
      this.screenWidth = document.body.clientWidth
      if (this.screenWidth < 991) {
        return '90%'
      } else if (this.screenWidth < 1400) {
        return '45%'
      } else {
        return '800px'
      }
    },
    loadListOptions({callback}) {
      callback()
    },
    setApplication(val, orgs) {
      const vm = this
      vm.orgList = orgs
      if (val) {
        vm.application = {...val}
      }
    },
    close() {
      this.$emit('close')
    },
    reset() {
      // 先清除校验，再清除表单，不然有奇怪的bug
      this.$refs.form.clearValidate()
      this.$refs.form.resetFields()
      this.application = this.initApplication()
    },
    submitForm() {
      const vm = this
      this.$refs.form.validate((valid) => {
        if (valid) {
          vm.editSubmit()
        } else {
          return false
        }
      })
    },
    editSubmit() {
      const vm = this
      if (vm.type === 'add') {
        vm.save()
      } else {
        vm.update()
      }
    },
    save() {
      const vm = this
      applicationApi.save(this.application)
        .then((response) => {
          const res = response.data
          if (res.isSuccess) {
            vm.isVisible = false
            vm.$message({
              message: vm.$t('tips.createSuccess'),
              type: 'success'
            })
            vm.$emit('success')
          }
        })
    },
    update() {
      applicationApi.update(this.application)
        .then((response) => {
          const res = response.data
          if (res.isSuccess) {
            this.isVisible = false
            this.$message({
              message: this.$t('tips.updateSuccess'),
              type: 'success'
            })
            this.$emit('success')
          }
        })
    }
  }
}
