import Pagination from "@/components/Pagination"
import DatasourceConfigEdit from "./edit"
import datasourceConfigApi from "@/api/DatasourceConfig.js"
import elDragDialog from '@/directive/el-drag-dialog'
import { downloadFile, initQueryParams } from '@/utils/commons'
export default {
  name: "DatasourceConfigManage",
  directives: { elDragDialog },
  components: { Pagination, DatasourceConfigEdit },
  filters: {
  },
  data () {
    return {
      dialog: {
        isVisible: false,
        title: ""
      },
      isTestConnect: false,
      preview: {
        isVisible: false,
        context: ''
      },
      tableKey: 0,
      queryParams: initQueryParams({}),
      selection: [],
      loading: false,
      tableData: {
        total: 0
      },
      enums: {
      }
    }
  },
  computed: {
  },
  mounted () {
    this.fetch()
  },
  methods: {
    editClose () {
      this.dialog.isVisible = false
    },
    editSuccess () {
      this.search()
    },
    onSelectChange (selection) {
      this.selection = selection
    },
    search () {
      this.fetch({
        ...this.queryParams
      })
    },
    reset () {
      this.queryParams = initQueryParams({})
      this.$refs.table.clearSort()
      this.$refs.table.clearFilter()
      this.search()
    },
    exportExcelPreview () {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }
      this.queryParams.extra.fileName = '导出数据源数据'
      datasourceConfigApi.preview(this.queryParams).then(response => {
        const res = response.data
        this.preview.isVisible = true
        this.preview.context = res.data
      })
    },
    exportExcel () {
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }
      this.queryParams.extra.fileName = '导出数据源数据'
      datasourceConfigApi.export(this.queryParams).then(response => {
        downloadFile(response)
      })
    },
    add () {
      this.$refs.edit.type = "add"
      this.dialog.title = this.$t("common.add")
      this.dialog.isVisible = true
    },
    singleDelete (row) {
      this.$refs.table.clearSelection()
      this.$refs.table.toggleRowSelection(row, true)
      this.batchDelete()
    },
    batchDelete () {
      if (!this.selection.length) {
        this.$message({
          message: this.$t("tips.noDataSelected"),
          type: "warning"
        })
        return
      }

      const readonlyIndex = this.selection.findIndex(item => item.readonly)

      if (readonlyIndex > -1) {
        this.$message({
          message: this.$t("tips.readonly"),
          type: "warning"
        })
        return
      }

      this.$confirm("删除数据源数据后，会导致运行中的租户无法正常运行，请谨慎删除", this.$t("common.tips"), {
        confirmButtonText: "坚决删除",
        cancelButtonText: "取消",
        type: "warning",
        closeOnClickModal: false,
        closeOnPressEscape: false,
        distinguishCancelAndClose: true
      })
        .then(() => {
          const ids = []
          let contain = false
          this.selection.forEach(item => {
            if (item.readonly) {
              contain = true
              return
            }
            ids.push(item.id)
          })
          if (contain) {
            this.$message({
              message: this.$t("tips.readonly"),
              type: "warning"
            })
          } else {
            this.delete(ids)
          }
        })
    },
    clearSelections () {
      this.$refs.table.clearSelection()
    },
    delete (ids) {
      datasourceConfigApi.delete(ids).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.$message({
            message: this.$t("tips.deleteSuccess"),
            type: "success"
          })
          this.search()
        }
      })
    },
    testConnect (row) {
      row.poolName = 'test'
      if (this.isTestConnect) {
        this.$message({
          message: '正在测试连接，请稍后',
          type: 'warn'
        })
        return
      }

      const loading = this.$loading({
        lock: true,
        text: 'Loading',
        spinner: 'el-icon-loading',
        background: 'rgba(0, 0, 0, 0.7)'
      });
      this.isTestConnect = true
      datasourceConfigApi.testConnect(row).then((response) => {
        const res = response.data
        if (res.isSuccess && res.data) {
          this.$message({
            message: '连接成功',
            type: 'success'
          })
        } else {
          this.$message({
            message: '连接失败',
            type: 'error'
          })
        }
      }).catch((error) => {
        console.log(error)
      }).finally(() => {
        this.isTestConnect = false;
        loading.close();
      })
    },
    copy (row) {
      this.$refs.edit.setDatasourceConfig(row)
      this.$refs.edit.type = "copy"
      this.dialog.title = this.$t("common.copy")
      this.dialog.isVisible = true
    },
    edit (row) {
      this.$refs.edit.setDatasourceConfig(row)
      this.$refs.edit.type = "edit"
      this.dialog.title = this.$t("common.edit")
      this.dialog.isVisible = true
    },
    fetch (params = {}) {
      this.loading = true
      if (this.queryParams.timeRange) {
        this.queryParams.extra.createTime_st = this.queryParams.timeRange[0]
        this.queryParams.extra.createTime_ed = this.queryParams.timeRange[1]
      }

      this.queryParams.current = params.current ? params.current : this.queryParams.current
      this.queryParams.size = params.size ? params.size : this.queryParams.size

      datasourceConfigApi.page(this.queryParams).then(response => {
        const res = response.data
        if (res.isSuccess) {
          this.tableData = res.data
        }
        // eslint-disable-next-line no-return-assign
      }).finally(() => this.loading = false)
    },
    sortChange (val) {
      this.queryParams.sort = val.prop
      this.queryParams.order = val.order
      if (this.queryParams.sort) {
        this.search()
      }
    },
    filterChange (filters) {
      for (const key in filters) {
        if (key.includes('.')) {
          const val = {}
          val[key.split('.')[1]] = filters[key][0]
          this.queryParams.model[key.split('.')[0]] = val
        } else {
          this.queryParams.model[key] = filters[key][0]
        }
      }
      this.search()
    },
    cellClick (row, column) {
      if (column['columnKey'] !== "selectionId") {
        return
      }
      let flag = false
      this.selection.forEach((item) => {
        if (item.id === row.id) {
          flag = true
          this.$refs.table.toggleRowSelection(row)
        }
      })

      if (!flag) {
        this.$refs.table.toggleRowSelection(row, true)
      }
    }
  }
}
